<?php
 namespace imwpcache\drivers; class redis implements driver { private $redis; public function connect($config) { if (!$this->redis) { $this->redis = new \Redis(); } if (!$this->redis->pconnect($config['host'], $config['port'], 5)) { return false; } return true; } public function set($key, $value, $expire) { return $this->redis->setex($key, $expire, $value); } public function get($key) { return $this->redis->get($key); } public function exists($key) { return $this->redis->exists($key); } public function delete($key) { return $this->redis->delete($key); } public function flush() { return $this->redis->flushDb(); } public function getStats() { return $this->redis->info(); } public function isExpire($key = null) { return false; } }