<?php
 namespace imwpf\builtin; use imwpf\admin\Options; use imwpf\modules\ShowID; use imwpf\modules\AntiSpam; class AdminPower { public function start() { $values = Options::getInstance('imwpf_options')->get('admin_power'); if (!$values) { return ; } if (!empty($values["showid"])) { foreach ($values["showid"] as $value) { if ($value == "show_id") { $ShowID = new ShowID(); $ShowID->run(); } } } if (!empty($values["spam_cmt"])) { $AntiSpam = new AntiSpam(); foreach ($values["spam_cmt"] as $value) { if ($value == "anti_link") { $AntiSpam->addBlockLinkComment(); } if ($value == "anti_nozh") { $AntiSpam->addBlockNoneChinese(); } } } if (!empty($values["spam_cmt_txt"])) { if (!isset($AntiSpam)) { $AntiSpam = new AntiSpam(); } $AntiSpam->addBlockSomeKeywords($values["spam_cmt_txt"]); } } } 