<?php
namespace imwpf\modules; class ShowID { public function run() { add_action('admin_init', array($this, 'show')); } public function show() { add_action('admin_head', array($this, 'addCSS')); add_filter('manage_posts_columns', array($this, 'addColumn')); add_action('manage_posts_custom_column', array($this, 'addValue'), 10, 2); add_filter('manage_pages_columns', array($this, 'addColumn')); add_action('manage_pages_custom_column', array($this, 'addValue'), 10, 2); add_filter('manage_media_columns', array($this, 'addColumn')); add_action('manage_media_custom_column', array($this, 'addValue'), 10, 2); add_filter('manage_link-manager_columns', array($this, 'addColumn')); add_action('manage_link_custom_column', array($this, 'addValue'), 10, 2); add_action('manage_edit-link-categories_columns', array($this, 'addColumn')); add_filter('manage_link_categories_custom_column', array($this, 'addReturnValue'), 10, 3); foreach (get_taxonomies() as $taxonomy) { add_action("manage_edit-${taxonomy}_columns", array($this, 'addColumn')); add_filter("manage_${taxonomy}_custom_column", array($this, 'addReturnValue'), 10, 3); } add_action('manage_users_columns', array($this, 'addColumn')); add_filter('manage_users_custom_column', array($this, 'addReturnValue'), 10, 3); add_action('manage_edit-comments_columns', array($this, 'addColumn')); add_action('manage_comments_custom_column', array($this, 'addValue'), 10, 2); } public function addColumn($cols) { $cols['ssid'] = 'ID'; return $cols; } public function addValue($column_name, $id) { if ($column_name == 'ssid') { echo $id; } } public function addReturnValue($value, $columnName, $id) { if ($columnName == 'ssid') { $value = $id; } return $value; } public function addCSS() { echo '<style type="text/css">#ssid { width: 50px; } </style>'; } } 